mapper.select:
SELECT * FROM( SELECT prod_id ,prod_name ,prod_price ,spec ,model_name ,DATE_FORMAT(reg_date, '%Y-%m-%d %H:%i:%S') reg_date FROM products
where ${searchCondition}
) T1 LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE products
prod_name = #{prodName}, prod_price = #{prodPrice}, spec = #{spec}, model_name = #{modelName},
WHERE prod_id = #{prodId}
